    const [selectedOption, setSelectedOption] = useState('中文');

    const options = ['中文', 'English'];

    const handleOptionClick = (option) => {
        setIsOpen(false);
        setSelectedOption(option);
        // onSelect(option);
    }

    return (
        <div className="dropdown-menu">
        <div className="dropdown-menu__selected" onClick={toggleMenu}>
          選擇語言
          <span className="dropdown-menu__arrow"></span>
        </div>
        {isOpen && (
          <ul className="dropdown-menu__options" onMouseLeave={toggleMenu}>
            <li className="dropdown-menu__option">中文</li>
            <li className="dropdown-menu__option">英文</li>
          </ul>
        )}
