import React, { useState } from 'react'
import "./index.scss"

export default function Login() {
    const [account, setAccount] = useState("")
    const [password, setPassword] = useState("")
    const [showPassword, setShowPassword] = useState(false)

    const handleSubmit = (event) => {
        event.preventDefault()
        console.log("submit", account, password)
    }

    const handleSave = (event, type) => {
        const { target } = event
        switch (type) {
            case "account":
                setAccount(target.value)
                break
            case "password":
                setPassword(target.value)
                break
            default:
                break
        }
    }

    const handlePassword = ( type) => {
        event.preventDefault()
        switch (type) {
            case "showPassword":
                setShowPassword(true)
                break
            case "hidePassword":
                setShowPassword(false)
                break
            default:
                break
        }
        console.log("click")
    }

    return (
        <div className='Login'>
            <form onSubmit={handleSubmit} className='login__container'>
                <div className='login__item'>
                    <h2 className='item__label'>帳號：</h2>
                    <input onChange={event => handleSave(event, "account")} className='item__input' value={account} maxLength={20} type="text" />
                </div>
                <div className='login__item'>
                    <h2 className='item__label'>密碼：</h2>
                    <input onChange={event => handleSave(event, "password")} className='item__input' value={password} maxLength={20} type="text" />
                    <button onClick={event => handlePassword(event, "showPassword")} className={`item__button--inactive ${showPassword ? "" : "acitve"}`}>隱藏</button>
                    <button onClick={event => handlePassword(event, "hidePassword")} className={`item__button--active ${showPassword ? "active" : ""}`}>顯示</button>
                </div>
                <button className='login__button'>登入</button>
            </form>
        </div>
    )
}
