import React, { useState } from 'react'
import "./index.scss"

export default function Login() {
    const [account, setAccount] = useState("")
    const [password, setPassword] = useState("")

    const handleSubmit = (event) => {
        event.preventDefault()
        console.log("submit",account,password)
    }

    const handleSave = (event, type) => {
        const { target } = event
        switch (type) {
            case "account":
                setAccount(target.value)
                break
            case "password":
                setPassword(target.value)
                break
            default:
                break
        }
    }

    return (
        <div className='Login'>
            <form onSubmit={handleSubmit} className='login__container'>
                <div className='login__item'>
                    <h2 className='item__label'>帳號：</h2>
                    <input onChange={event => handleSave(event, "account")} className='item__input' value={account} type="text" />
                </div>
                <div className='login__item'>
                    <h2 className='item__label'>密碼：</h2>
                    <input onChange={event => handleSave(event, "password")} className='item__input' value={password} type="text" />
                </div>
                <button className='login__button'>登入</button>
            </form>
        </div>
    )
}
