import React, { useState } from 'react'
import "./index.scss"

export default function DropdownMenu() {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState("");

    const handleOptionClick = (option) => {
      setSelectedOption(option);
      setIsOpen(false);
    };

    const toggleDropdown = () => {
      setIsOpen(!isOpen);
    };


    return (
        <div className="dropdown">
        <div className="dropdown__selected" onClick={toggleDropdown}>
          <span>{selectedOption || "請選擇"}</span>
          <i className={`fas fa-chevron-${isOpen ? "up" : "down"}`}></i>
        </div>
        {isOpen && (
          <ul className="dropdown__list">
            <li
              className={`dropdown__option ${
                selectedOption === "中文" ? "dropdown__option--selected" : ""
              }`}
              onClick={() => handleOptionClick("中文")}
            >
              中文
            </li>
            <li
              className={`dropdown__option ${
                selectedOption === "英文" ? "dropdown__option--selected" : ""
              }`}
              onClick={() => handleOptionClick("英文")}
            >
              英文
            </li>
          </ul>
        )}
      </div>
    )
}
