import { createSlice } from '@reduxjs/toolkit'

const data =
    localStorage.getItem("locales") !== null
        ? JSON.parse(localStorage.getItem("locales"))
        : "tc"

const initialState = {
    value: data,
}

export const dropdownMenuSlice = createSlice({
    name: 'dropdownMenu',
    initialState,
    reducers: {
        changeDropdownMenu: (state, data) => {
            switch (data.payload) {
                case "中文":
                    state.value = "tc"
                    break
                case "English":
                    state.value = "en"
                    break
                default:
                    break
            }
            localStorage.setItem("locales", JSON.stringify(state.value))
        },
    },
})

export const { changeDropdownMenu } = dropdownMenuSlice.actions

export default dropdownMenuSlice.reducer
