import React, { useState, useEffect, useRef } from 'react'
import "./index.scss"

import { useTranslation } from 'react-i18next';

export default function DropdownMenu() {

  const { i18n } = useTranslation()
  const [languages,setLanguages] = useState([])

  useEffect(() => {
    // i18n.changeLanguage("zh-TW")
    i18n.changeLanguage(localStorage.getItem("i18nextLng"))
    const resources = i18n.services.resourceStore.data
    // const languages = Object.keys(resources)
    setLanguages(Object.keys(resources))
  }, [i18n,set])


  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(localStorage.getItem("i18nextLng"));

  const renderOption = option => {
    switch (option) {
      case "zh-TW":
        return "中文"
      case "en":
        return "English"
      default:
        return
    }
  }

  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleOutsideClick = event => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleOutsideClick);

    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, [dropdownRef]);

  const handleOptionClick = option => {
    setIsOpen(false);
    console.log(option)
    setSelectedOption(option);
    i18n.changeLanguage(option)
  }

  return (
    <div className='dropdown-menu' ref={dropdownRef}>
      <button className="dropdown-menu__toggle" onClick={() => setIsOpen(!isOpen)}>
        <span className='dropdown-menu__toggle-label'>{renderOption(selectedOption)}</span>
        <div className='dropdown-menu__toggle-icon'>
          <svg className={`caret ${isOpen ? "active" : ""}`} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 256 512">
            <path d="M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z" />
          </svg>
        </div>
      </button>
      {isOpen && (
        <ul className="dropdown-menu__list">
          {languages.map(option => (
            <li
              key={option}
              className={`dropdown-menu__list-item ${option === selectedOption ? 'active' : ''}`}
              onClick={() => handleOptionClick(option)}
            >
              {renderOption(option)}
            </li>
          ))}
        </ul>
      )}
    </div>
  )
}
