import React, { useState } from 'react'
import "./index.scss"

export default function DropdownMenu() {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState('中文');

    const options = ['中文', 'English'];

    const handleOptionClick = (option) => {
        setIsOpen(false);
        setSelectedOption(option);
        // onSelect(option);
    }

    return (
        <div className='dropdown-menu'>
            <button className="dropdown-menu__toggle" onClick={() => setIsOpen(!isOpen)}>
                {selectedOption}
                <div className='dropdown-menu__toggle-list'>
                    <svg className='dropdown-menu__caret' xmlns="http://www.w3.org/2000/svg" viewBox="0 0 320 512">
                        <path d="M182.6 137.4c-12.5-12.5-32.8-12.5-45.3 0l-128 128c-9.2 9.2-11.9 22.9-6.9 34.9s16.6 19.8 29.6 19.8H288c12.9 0 24.6-7.8 29.6-19.8s2.2-25.7-6.9-34.9l-128-128z" />
                    </svg>
                </div>
            </button>
            {isOpen && (
                <ul className="dropdown-menu__list">
                    {options.map(option => (
                        <li
                            key={option}
                            className={`dropdown-menu__item $Poption === selectedOption ? 'active' : ''`}
                            onClick={() => handleOptionClick(option)}
                        >
                            {option}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    )
}
